import{join}from"path";import{assembleFiles}from"codesandboxer-fs";const wrapCode=n=>`import React from 'react';\n\nconst doczStyles = {\n  margin: '0 3px',\n  padding: '4px 6px',\n  fontFamily: '"Source Code Pro", monospace',\n  fontSize: 14,\n};\n\nconst App = ({ children }) => (\n  <div style={doczStyles}>\n    {children && typeof children === 'function' ? children() : children}\n  </div>\n)\n\nexport default () => (\n  <App>\n    ${n.split("\n").join("\n    ")}\n  </App>\n)`;function getSandboxFiles(n,o,e){const t=[...o,wrapCode(n)].join("\n"),s=join(e,"codesandbox.example.csb.js");return assembleFiles(s,{extensions:[".js",".ts",".jsx",".tsx"],contents:t})}const getSandboxImportInfo=async(n,o,e)=>{let t;try{const{parameters:s}=await getSandboxFiles(n,o,e);t=s}catch(n){console.error("Could not create Open in CodeSandbox",n)}return t};export{getSandboxImportInfo};
